import React,{useState} from 'react'
import './index.css'
import ViewMatches from './ViewMatches'
import Series from './series'

function Match() {

    const [viewMatches, setviewMatches] = useState(true)
    const [series, setSeries] = useState(false)
    const [pointTable, setpointTable] = useState(false)
    const [schedule, setSchedule] = useState(false)

    const matches = [{
        nthmatch: "45th Match",
        series: "Indian Premier League 2020",
        team1: "RR",
        team2: "MI",
        tosschoose: "Mumbai Indians opt to bat",
        team1run: 82,
        team1wickets: 1,
        team1overs: 9.2,
        team2run: 102,
        team2wickets: 5,
        team2overs: 20
    },
    {
        nthmatch: "45th Match",
        series: "Indian Premier League 2020",
        team1: "RR",
        team2: "MI",
        tosschoose: "Mumbai Indians opt to bat",
        team1run: 82,
        team1wickets: 1,
        team1overs: 9.2,
        team2run: 102,
        team2wickets: 5,
        team2overs: 20
    },
    {
        nthmatch: "45th Match",
        series: "Indian Premier League 2020",
        team1: "RR",
        team2: "MI",
        tosschoose: "Mumbai Indians opt to bat",
        team1run: 82,
        team1wickets: 1,
        team1overs: 9.2,
        team2run: 102,
        team2wickets: 5,
        team2overs: 20
    }]

    const ClickPointtable = ()=>{
        setSeries(true)
        setpointTable(true)
        setviewMatches(false)
        setSchedule(false)
    }

    const ClickSchedule = ()=>{
        setSeries(true)
        setSchedule(true)
        setpointTable(false)
        setviewMatches(false)        
    }

    return (
        <div className="w3-container">
            {
                viewMatches && <ViewMatches matches={matches} ClickPointtable={ClickPointtable} ClickSchedule={ClickSchedule} />
            }
            {
                series && <Series pointTable={pointTable} schedule={schedule} />
            }   
        </div>
    )
}

export default Match
