import React, { useState } from 'react'
import Matches from './Matches'
import PointsTable from './PointsTable'
import SeriesMenus from './SeriesMenus'
import Squads from './Squads'
import Stats from './Stats'
import Venues from './Venues'
import './index.css'

function Series({pointTable,schedule}) {
    const [menu, setMenu] = useState("matches")
    const ClickMenu = (data)=>{
        setMenu(data)
    }
    if(pointTable){
        setMenu("points")
    }
    if(schedule){
        setMenu("matches")
    }
    return (
        <div>
            <SeriesMenus ClickMenu={ClickMenu} />
            {
                pointTable && <PointsTable />
            }
            {
                schedule && <Matches />
            }
            {
                menu === "matches" && <Matches />
            }
            {
                menu === "squads" && <Squads />
            }
            {
                menu === "stats" && <Stats />
            }
            {
                menu === "venues" && <Venues />
            }
            {
                menu === "points" && <PointsTable />
            }

        </div>
    )
}

export default Series
